import axios from 'axios';
import { environment } from 'src/environments/environment';
import Swal from 'sweetalert2';
import * as i0 from "@angular/core";
export class MasterService {
    constructor() {
        this.host = environment.host;
        this.headers = { headers: { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' } };
        // functions
        this.get = async (section) => {
            debugger;
            let x = await axios.get(`${this.host}${section}`, this.headers);
            debugger;
            return x;
        };
        // control request
        this.requestManage = (res) => {
            debugger;
            if (res) {
                if (res == 200) {
                    return res.data;
                }
                else {
                    this.showAlert('We had a problem', res.error, 'warning');
                }
            }
            else {
                this.showAlert('API no response', 'The api did not respond', 'warning');
            }
        };
        // functions
        this.showAlert = (title, text, icon) => {
            Swal.fire({
                title: title,
                text: text,
                icon: icon,
                confirmButtonText: 'Continue'
            });
        };
        // confirm alert
        this.confirmAlert = (title, text, button, callback) => {
            Swal.fire({
                title: title,
                text: text,
                icon: 'question',
                showCancelButton: true,
                confirmButtonText: button,
            }).then((result) => {
                /* Read more about isConfirmed, isDenied below */
                if (result.isConfirmed) {
                    Swal.fire('Success!', '', 'success');
                    callback(true);
                }
            });
        };
        axios.defaults.headers.post['Content-Type'] = 'application/json;charset=utf-8';
        axios.defaults.headers.post['Access-Control-Allow-Origin'] = '*';
    }
}
